// Function Node: Smart Delay Calculation
const checkout = $input.first().json;
const cartValue = parseFloat(checkout.total_price);
const customerTier = checkout.customer?.tags?.includes('VIP') ? 'VIP' : 'Standard';

// Dynamic delay based on cart value and customer tier
let delayMinutes = 15; // Default

if (cartValue > 100) {
  delayMinutes = 30; // Higher value carts get longer delay
}

if (customerTier === 'VIP') {
  delayMinutes = 45; // VIP customers get more time
}

return [{
  json: {
    ...checkout,
    delayMinutes: delayMinutes,
    scheduledSendTime: new Date(Date.now() + delayMinutes * 60000).toISOString()
  }
}];

// Function Node: Validate Customer Data
const webhookData = $input.first().json;

// Extract the actual checkout data from the webhook body
const checkout = webhookData.body;

// Validation checks with correct data paths
const validationResults = {
  hasEmail: !!checkout.email && checkout.email.includes('@'),
  hasCustomerId: !!checkout.customer?.id,
  hasProducts: checkout.line_items && checkout.line_items.length > 0,
  isAbandoned: !checkout.completed_at,
  cartValue: checkout.total_price ? parseFloat(checkout.total_price) : 0
};

// Check if all validations pass
const isValid = validationResults.hasEmail && 
                validationResults.hasCustomerId && 
                validationResults.hasProducts && 
                validationResults.isAbandoned &&
                validationResults.cartValue > 0;

if (!isValid) {
  // Log validation failure details
  console.log('Validation failed details:', {
    validationResults: validationResults,
    checkoutData: checkout,
    emailFound: checkout.email,
    customerIdFound: checkout.customer?.id,
    productsFound: checkout.line_items?.length
  });

  return [{
    json: {
      error: 'Validation failed',
      validationResults: validationResults,
      skipWorkflow: true,
      debugInfo: {
        dataStructure: 'body object detected',
        actualEmail: checkout.email,
        actualCustomerId: checkout.customer?.id,
        actualCartValue: checkout.total_price
      }
    }
  }];
}

// Add validation metadata to checkout data and pass the body content forward
return [{
  json: {
    customerId: checkout.customer.id,
    isValid,
    validation: {
      passed: true,
      checkedAt: new Date().toISOString(),
      cartValue: validationResults.cartValue
    },
  }
}];

// Simplified Customer Data Enrichment (No Node References)
const inputData = $input.first().json;
const requestedData = $node["Webhook Trigger"].json;

console.log('Input data keys:', Object.keys(inputData));
console.log('Input data sample:', JSON.stringify(inputData).substring(0, 200));

// Extract customer data
const customer = inputData.customer;

// Create checkout data from what we have
const checkoutData = requestedData.body;

// Calculate customer metrics
const totalSpent = parseFloat(customer.total_spent);
const ordersCount = customer.orders_count;
const avgOrderValue = ordersCount > 0 ? totalSpent / ordersCount : 0;

// Determine loyalty tier
let loyaltyTier = 'New';
if (totalSpent > 500) loyaltyTier = 'VIP';
else if (totalSpent > 200) loyaltyTier = 'Valued';
else if (ordersCount > 0) loyaltyTier = 'Returning';

// Analyze abandoned cart
const abandonedProducts = (checkoutData.line_items).map(item => ({
  title: item.title,
  vendor: item.vendor,
  product_type: item.product_type,
  price: parseFloat(item.price),
  quantity: item.quantity,
  tag: item.tag,
  alt: item.alt,
  variant_title: item.variant_title
}));

// Calculate cart insights
const cartValue = parseFloat(checkoutData.total_price);
const productCount = abandonedProducts.reduce((sum, item) => sum + item.quantity, 0);
const averageProductPrice = productCount > 0 ? cartValue / productCount : 0;

// Determine customer engagement level
let engagementLevel = 'Medium';
if (ordersCount >= 5) engagementLevel = 'High';
else if (ordersCount <= 1) engagementLevel = 'Low';

// Create seasonal context
const currentMonth = new Date().getMonth() + 1;
let seasonalContext = '';
if ([12, 1, 2].includes(currentMonth)) seasonalContext = 'Winter skincare needs';
else if ([3, 4, 5].includes(currentMonth)) seasonalContext = 'Spring refresh';
else if ([6, 7, 8].includes(currentMonth)) seasonalContext = 'Summer protection';
else seasonalContext = 'Fall preparation';

// Determine geographic context
const location = customer.default_address;
const cityContext = location.city ? `${location.city}, ${location.country}` : location.country;

// Helper functions
function calculateProfileCompleteness() {
  let score = 0;
  if (customer.first_name) score += 20;
  if (customer.phone) score += 15;
  if (location.city) score += 15;
  if (ordersCount > 0) score += 30;
  return score;
}

function calculateUrgencyIndicators() {
  const indicators = [];

  if (cartValue > avgOrderValue * 1.5) {
    indicators.push('high_value_cart');
  }

  if (abandonedProducts.some(p => p.title.toLowerCase().includes('limited') || 
                                   p.title.toLowerCase().includes('sale'))) {
    indicators.push('limited_time_offer');
  }

  const daysSinceAbandoned = Math.floor((new Date() - new Date(checkoutData.created_at)) / (1000 * 60 * 60 * 24));
  if (daysSinceAbandoned >= 2) {
    indicators.push('time_sensitive');
  }

  return indicators;
}

function identifyPersonalizationOpportunities() {
  const opportunities = [];

  if (ordersCount === 0) {
    opportunities.push('first_time_buyer');
  }

  if (loyaltyTier === 'VIP') {
    opportunities.push('vip_treatment');
  }

  return opportunities;
}

// Create enriched customer profile
const enrichedProfile = {
  // Basic customer info
  customer: {
    id: customer.id,
    email: customer.email,
    first_name: customer.first_name,
    last_name: customer.last_name,
    phone: customer.phone,
    city: location.city,
    country: location.country,
    province: location.province,
    zip: location.zip,
    tags: customer.tags,
    created_at: customer.created_at,
  },

  // Purchase analytics
  purchase_analytics: {
    total_spent: totalSpent,
    orders_count: ordersCount,
    avg_order_value: Math.round(avgOrderValue * 100) / 100,
    engagement_level: engagementLevel,
    loyalty_tier: loyaltyTier
  },

  // Beauty profile
  beauty_profile: {
    seasonal_context: seasonalContext,
    location_context: cityContext
  },

  // Abandoned cart details
  abandoned_cart: {
    id: checkoutData.id,
    token: checkoutData.token,
    created_at: checkoutData.created_at,
    updated_at: checkoutData.updated_at,
    total_price: cartValue,
    currency: checkoutData.currency,
    products: abandonedProducts,
    product_count: productCount,
    avg_product_price: Math.round(averageProductPrice * 100) / 100,
    checkout_url: checkoutData.abandoned_checkout_url,
    cart_vs_avg_order: avgOrderValue > 0 ? Math.round((cartValue / avgOrderValue) * 100) / 100 : 0
  },

  // Purchase history context
  purchase_history: {
    repeat_customer: ordersCount > 1,
    high_value_customer: totalSpent > 300
  },

  // Personalization metadata
  personalization_data: {
    profile_completeness: calculateProfileCompleteness(),
    urgency_indicators: calculateUrgencyIndicators(),
    personalization_opportunities: identifyPersonalizationOpportunities()
  }
};

return [{ json: enrichedProfile }];

// Function Node: Prepare AI Prompt
const profile = $input.first().json;

// Extract key data safely with fallbacks
const customer = profile.customer;
const analytics = profile.purchase_analytics;
const beautyProfile = profile.beauty_profile;
const cart = profile.abandoned_cart;
const opportunities = profile.personalization_data?.personalization_opportunities;

// Build dynamic prompt based on customer data
let prompt = `Hi Sofia! I need your help creating a personalized message for a customer who abandoned their checkout. Here's what I know:\n\n`;

// Customer basics
prompt += `**Customer:** ${customer.first_name} ${customer.last_name}\n`;
if (beautyProfile.location_context) {
  prompt += `**Location:** ${beautyProfile.location_context}\n`;
}
if (beautyProfile.seasonal_context) {
  prompt += `**Season:** ${beautyProfile.seasonal_context}\n`;
}

prompt += `\n`;

// Purchase history and loyalty
if (analytics.orders_count > 0) {
  prompt += `**Their Beauty Journey with Us:**\n`;
  prompt += `- ${analytics.orders_count} previous orders totaling $${analytics.total_spent}\n`;
  prompt += `- Customer tier: ${analytics.loyalty_tier}\n`;
  prompt += `- Average order value: $${analytics.avg_order_value}\n`;
  prompt += `- Engagement level: ${analytics.engagement_level}\n`;
} else {
  prompt += `**New Customer:** This would be their very first purchase with us! ðŸŽ‰\n`;
}

// Abandoned cart details
prompt += `\n**Their Abandoned Cart ($${cart.total_price || '0'}):**\n`;
if (cart.products && cart.products.length > 0) {
  cart.products.forEach((product, index) => {
    prompt += `${index + 1}. ${product.title}`;
    if (product.vendor) prompt += ` by ${product.vendor}`;
    prompt += ` - $${product.price}`;
    if (product.quantity > 1) prompt += ` (qty: ${product.quantity})`;
  });
} else {
  prompt += `- Cart details not available\n`;
}

// Add personalization insights
if (opportunities.length > 0) {
  prompt += `\n**Personalization Opportunities:** ${opportunities.join(', ')}\n`;
}

// Special context for different customer tiers
if (analytics.loyalty_tier === 'VIP') {
  prompt += `\n**VIP Note:** This is a high-value customer - acknowledge their loyalty and provide premium-level service and insights.\n`;
} else if (analytics.loyalty_tier === 'New') {
  prompt += `\n**New Customer Note:** Help them feel welcome and confident about choosing our brand. Focus on building trust.\n`;
}

// Instructions for message creation
prompt += `\n**Create a personalized message (150-200 words) that:**

1. **Personal Greeting:** Use their name and acknowledge their relationship with the brand
2. **Product Connection:** Show how the abandoned items work together or solve specific beauty goals
3. **Expert Tips:** Include 1-2 beauty tips or insights about the products or how to use them
4. **Gentle Motivation:** Create a natural reason to complete the purchase (timing, availability, routine benefits)
5. **Easy Action:** Include checkout completion in a natural, helpful way

**Writing Guidelines:**
- Tone: Warm, knowledgeable, personal (like texting a beauty-savvy friend)
- Focus: Genuinely helpful advice, not sales pressure
- Style: Natural, conversational, confident expertise
- Length: 150-200 words exactly
- Avoid: Generic phrases, excessive exclamation points, obvious sales tactics

**The message should feel like Sofia genuinely noticed they left items behind and wants to help them achieve their beauty goals.**`;

// Return enriched data with prompt
return [{
  json: {
    ...profile,
    ai_prompt: prompt,
    prompt_generated_at: new Date().toISOString()
  }
}];

