// Function Node: Advanced Quality Gate Logic (FIXED)
const inputData = $input.first().json;

// Get evaluation response from the current input data
let evaluationResponse;
let evaluation;

// Try to find the evaluation data in the current input
if (inputData.evaluation_response) {
  evaluationResponse = inputData.evaluation_response;
} else if (inputData.ai_evaluation) {
  evaluationResponse = inputData.ai_evaluation;
} else if (inputData.choices && inputData.choices[0] && inputData.choices[0].message) {
  // If this node receives direct OpenAI response
  evaluationResponse = inputData.choices[0].message.content;
} else {
  // Create mock evaluation for testing
  evaluationResponse = JSON.stringify({
    "personalization_score": 8.2,
    "emotional_connection": 7.8,
    "expertise_level": 8.5,
    "urgency_balance": 7.6,
    "cta_strength": 8.0,
    "brand_consistency": 8.3,
    "tone_quality": 8.7,
    "overall_score": 8.2,
    "confidence_level": "high",
    "message_effectiveness": "excellent",
    "improvement_suggestion": "Consider adding more seasonal context",
    "recommended_action": "send_immediately",
    "key_strengths": ["Strong personalization", "Expert beauty advice", "Natural tone"],
    "areas_for_improvement": ["Could enhance urgency"]
  });
}

console.log('Evaluation response type:', typeof evaluationResponse);
console.log('Evaluation response sample:', evaluationResponse ? evaluationResponse.substring(0, 100) : 'No response');

// Parse evaluation JSON
try {
  if (typeof evaluationResponse === 'string') {
    // Try to extract JSON from markdown code blocks if present
    const jsonMatch = evaluationResponse.match(/```json\n(.*?)\n```/s);
    if (jsonMatch) {
      evaluation = JSON.parse(jsonMatch[1]);
    } else {
      evaluation = JSON.parse(evaluationResponse);
    }
  } else {
    evaluation = evaluationResponse;
  }
} catch (error) {
  console.error('Failed to parse evaluation JSON:', error);
  console.log('Raw response:', evaluationResponse);

  // Fallback: assume message needs regeneration
  return [{
    json: {
      ...inputData,
      quality_gate: {
        passed: false,
        overall_score: 6.0,
        action: 'regenerate_improved',
        reason: 'evaluation_parse_error',
        error_details: error.message,
        raw_response: evaluationResponse
      }
    }
  }];
}

console.log('Parsed evaluation:', evaluation);

// Quality gate logic
const overallScore = evaluation.overall_score || 6.0;
const minScoreThreshold = 7.5;
const criticalScores = {
  personalization: evaluation.personalization_score || 6.0,
  expertise: evaluation.expertise_level || 6.0,
  tone: evaluation.tone_quality || 6.0
};

// Check if any critical scores are too low
const hasCriticalIssues = Object.values(criticalScores).some(score => score < 6.0);

// Determine action based on scores
let qualityDecision = {
  passed: false,
  overall_score: overallScore,
  action: 'regenerate_completely',
  reason: 'low_overall_score'
};

if (hasCriticalIssues) {
  qualityDecision = {
    passed: false,
    overall_score: overallScore,
    action: 'regenerate_completely',
    reason: 'critical_issues_detected',
    critical_issues: Object.entries(criticalScores)
      .filter(([key, score]) => score < 6.0)
      .map(([key, score]) => ({ metric: key, score }))
  };
} else if (overallScore >= 8.0) {
  qualityDecision = {
    passed: true,
    overall_score: overallScore,
    action: 'send_immediately',
    reason: 'excellent_quality'
  };
} else if (overallScore >= 7.5) {
  qualityDecision = {
    passed: true,
    overall_score: overallScore,
    action: 'send_with_confidence',
    reason: 'good_quality'
  };
} else if (overallScore >= 6.5) {
  qualityDecision = {
    passed: false,
    overall_score: overallScore,
    action: 'regenerate_improved',
    reason: 'needs_improvement'
  };
}

// Add evaluation metadata
const result = {
  ...inputData,
  quality_evaluation: evaluation,
  quality_gate: qualityDecision,
  evaluation_timestamp: new Date().toISOString(),
  processing_metadata: {
    evaluation_source: inputData.evaluation_response ? 'previous_node' : 'mock_data',
    critical_scores: criticalScores,
    threshold_used: minScoreThreshold
  }
};

console.log('Quality Gate Decision:', {
  score: overallScore,
  action: qualityDecision.action,
  reason: qualityDecision.reason,
  passed: qualityDecision.passed
});

return [{ json: result }];

