// Helper function to safely extract values
function safeExtract(obj, path, defaultValue = null) {
  try {
    return path.split('.').reduce((current, key) => {
      if (current === null || current === undefined) return defaultValue;
      return current[key];
    }, obj) || defaultValue;
  } catch (error) {
    return defaultValue;
  }
}

